package com.zdmoney.webservice.api.dto.customer;import lombok.Data;import java.io.Serializable;@Datapublic class UserInfoDTO implements Serializable {    private Long userId;    private String userLevel;    public String getUserLevel() {        String level = "";        if ("0".equals(this.userLevel)) {            level = "理财师";        } else if ("1".equals(this.userLevel)) {            level = "理财师一级客户";        } else if ("2".equals(this.userLevel)) {            level = "理财师二级客户";        } else if ("3".equals(this.userLevel)) {            level = "互联网客户";        } else if ("4".equals(this.userLevel)) {            level = "互联网员工";        }        return level;    }}