package com.zdmoney.models.customer;import com.zdmoney.constant.AppConstants;import com.zdmoney.models.sys.SysParameter;import java.io.Serializable;import java.math.BigDecimal;import java.util.List;public class CustomerLimitInfo implements Serializable {    /**     * 测评类型     */    private String riskType;    /**     * 最大投资金额     */    private BigDecimal maxAmt;    /**     * 再投总金额     */    private BigDecimal totalAmt;    /**     * 剩余额度     */    private BigDecimal surplusAmt;    /**     * 测评等级与投资关系     */    private List<SysParameter> paramsList;    private Boolean isAlert = false;    private String riskTestUrl;    public String getRiskType() {        String type = "";        if ("A".equals(riskType)) {            type = AppConstants.riskType.RISK_TYPE_A;        } else if ("B".equals(riskType)) {            type = AppConstants.riskType.RISK_TYPE_B;        } else if ("C".equals(riskType)) {            type = AppConstants.riskType.RISK_TYPE_C;        } else if ("D".equals(riskType)) {            type = AppConstants.riskType.RISK_TYPE_D;        } else if ("E".equals(riskType)) {            type = AppConstants.riskType.RISK_TYPE_E;        }        return type;    }    public void setRiskType(String riskType) {        this.riskType = riskType;    }    public BigDecimal getMaxAmt() {        return maxAmt;    }    public void setMaxAmt(BigDecimal maxAmt) {        this.maxAmt = maxAmt;    }    public BigDecimal getTotalAmt() {        return totalAmt;    }    public void setTotalAmt(BigDecimal totalAmt) {        this.totalAmt = totalAmt;    }    public BigDecimal getSurplusAmt() {        return surplusAmt;    }    public void setSurplusAmt(BigDecimal surplusAmt) {        this.surplusAmt = surplusAmt;    }    public List<SysParameter> getParamsList() {        return paramsList;    }    public void setParamsList(List<SysParameter> paramsList) {        this.paramsList = paramsList;    }    public Boolean getAlert() {        return isAlert;    }    public void setAlert(Boolean alert) {        isAlert = alert;    }    public String getRiskTestUrl() {        return riskTestUrl;    }    public void setRiskTestUrl(String riskTestUrl) {        this.riskTestUrl = riskTestUrl;    }}